import { withFormik, Field, Form } from 'formik'
import OutForm from './OutForm'
import styles from './styles.module.css'
import * as Yup from 'yup'
import ReactLoading from 'react-loading'

function TemplateForms({values, touched, isSubmitting, errors}){
    const constraintTextHolder = "Constraints \ne.g.\nvalue[1] == value[2]\nvalue[2] + value[3] == 4"
    const constraintPythonHolder = `Constraints \ne.g.\nconstraint = ""\n for i in range(5):\n    constraint += f'value[i] != 1\nreturn constraint`
    const domainTextHolder = "Domain\ne.g.\nvalue[1] = [1,2,3]\nvalue[2] = [2,4,1]"
    const domainPythonHolder = `Domain\ne.g.\ndomains = ""\nfor i in range(3):\n    domains += f'value[{i + 1}] = [1,2,3]\nreturn domains`

    return (
        <div className={styles.container}>
            <OutForm value={values.outState}/>
            <Form className={styles.formStyle}>
                {<ReactLoading type={"bars"} className={styles.centerAnimation} color="#252525"/>}
                {/* <label name="Algo" className = {styles.Algo} >
                <Field component="select" name="algorithm" className={styles.dropDown}>
                    <option value="solve_dfs" className={styles.dropDownContent}>Depth First Search</option>
                </Field>
                Select an Algorithm
                </label> */}
                {/* <br/>
                <Field component="select" name="format" className={styles.dropDown}>
                    <option value="text">Text</option>
                    <option value="python">Python</option>
                </Field>
                <br/>
                {touched.variables && errors.variables && <p className={styles.error}>{errors.variables}</p>} */}
                <Field name="variables" placeholder="Variables" className={styles.variable}/>
                {/* <br/> 
                <Field component="textarea" name="domain" placeholder={values.format === 'text'? domainTextHolder: domainPythonHolder} className={styles.domain}/>
                <br/>
                <Field component="textarea" name="constraints" placeholder={values.format === 'text'? constraintTextHolder: constraintPythonHolder} className={styles.constraint}></Field>
                <br/>
                <button disabled={isSubmitting} type = "submit" className="btn">Run</button> */}
            </Form>
        </div>

    )
}

const Forms = withFormik({
    mapPropsToValues(){
        return {
            variables: '',
            domain:'',
            constraints: '',
            algorithm: 'solve_dfs',
            format: 'text',
            outState: '',
        }
    },
    validationSchema: Yup.object().shape({
        variables: Yup.string().matches(/^\d+$/, '*Must be an integer').required('*Required'),
        constraints: Yup.string(),
    }),
    async handleSubmit(values, {setSubmitting, setFieldValue}){
        setSubmitting(true)
        let response = await fetch(`/api/${values.variables}/${values.algorithm}/${values.format}/?constraint=${encodeURIComponent(values.constraints)}&domain=${encodeURIComponent(values.domain)}`)
        if(response.ok){
            response = await response.json()
            if(response['timed_out']){
                setFieldValue('outState', 'Timed out...')
            }
            else if(!response['done']){
                setFieldValue('outState', 'No solution exists...')
            }
            else {
                setFieldValue('outState', response['solution'])
            }
        }
        else {
            response = await response.text()
            setFieldValue('outState', response)
        }
        setSubmitting(false)
    }
})(TemplateForms)

export default Forms
