import { withFormik, Field, Form } from 'formik'
import styles from './styles.module.css'
import * as Yup from 'yup'

function TemplateForms({touched, isSubmitting, errors}){
    return (
        <Form className={styles.formStyle}>
            <label>
            <Field component="select" name="algorithm">
                <option value="dfs">Depth First Search</option>
            </Field>
            Algorithm
            </label>
            <br/>
            {touched.variables && errors.variables && <p className={styles.error}>{errors.variables}</p>}
            <Field type="text" name="variables" placeholder="Variables" className={styles.variable}/>
            <br/> 
            <Field type="text" component="textarea" name="constraints" placeholder={"Constraints \n e.g. value[1] == value[2]"} className={styles.constraint}></Field>
            <br/>
            <button disabled={isSubmitting} type = "submit" className="btn">Run</button>
        </Form>
    )
}

const Forms = withFormik({
    mapPropsToValues(){
        return {
            variables: '',
            constraints: '',
            algorithm: '',
        }
    },
    validationSchema: Yup.object().shape({
        variables: Yup.string().matches(/^\d+$/, '*Must be an integer').required('*Required'),
        constraints: Yup.string(),
    }),
    handleSubmit(values, {setSubmitting}){
        console.log(values)
        setSubmitting(false)
    }
})(TemplateForms)

export default Forms
