import { withFormik, Field, Form } from 'formik'
import OutForm from './OutForm'
import styles from './styles.module.css'
import * as Yup from 'yup'
import ReactLoading from 'react-loading'

function TemplateForms({values, touched, isSubmitting, errors}){
    return (
        <div>
            <Form className={styles.formStyle}>
                <label name="Algo" className = {styles.Algo} >
                <Field component="select" name="algorithm" className={styles.dropDown}>
                    <option value="dfs" className={styles.dropDownContent}>Depth First Search</option>
                </Field>
                Select an Algorithm
                </label>
                <br/>
                <Field component="select" name="format" className={styles.dropDown}>
                    <option value="text">Text</option>
                    <option value="python">Python</option>
                </Field>
                <br/>
                {touched.variables && errors.variables && <p className={styles.error}>{errors.variables}</p>}
                <Field name="variables" placeholder="Variables" className={styles.variable}/>
                <br/> 
                <Field component="textarea" name="domain" placeholder={"Domain \n e.g. value[1 ,2, 3, 4]"} className={styles.domain}/>
                <br/>
                <Field component="textarea" name="constraints" placeholder={"Constraints \n e.g. value[1] == value[2]"} className={styles.constraint}></Field>
                <br/>
                <button disabled={isSubmitting} type = "submit" className="btn btn-danger">Run</button>
            </Form>
            <OutForm value={values}/>
            {isSubmitting && <ReactLoading type={"bars"} className={styles.centerAnimation} color="#252525"/>}
        </div>

    )
}

const Forms = withFormik({
    mapPropsToValues(){
        return {
            variables: '',
            domain:'',
            constraints: '',
            algorithm: 'dfs',
            format: 'text'
        }
    },
    validationSchema: Yup.object().shape({
        variables: Yup.string().matches(/^\d+$/, '*Must be an integer').required('*Required'),
        constraints: Yup.string(),
    }),
    handleSubmit(values, {setSubmitting}){
        setTimeout(()=> {
            setSubmitting(false)
        }
        ,5000)
    }
})(TemplateForms)

export default Forms
