  const [getValues, setValues] = useThunkReducer(
    createValuesReducer(),
    {},
    (initialState) => {
      if (typeof defaultValues === "function") {
        return defaultValues();
      }

      return defaultValues || initialState;
    }
  );

  return {
    getValues,
    setValues,
  };
};
